package mYPackage2;

public class Nayeem_Script14 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		byte b;  // Byte = -128 to 127
		b=127;
		System.out.println("The byte value is: "+b);
		
		short s;  // Short = 32767
		s=32767;
		System.out.println("The short value is: "+s);
		
		long l;  // Long = -2^63 to 2^63-1
		l=32767;
		System.out.println("The long value is: "+l);
		
		double d;
		d=10.45d;
		System.out.println("The double value is: "+d);
		
		float f;
		f = 10.2f;
		System.out.println("The short value is: "+f);
		
		char c = 'x';
		System.out.println("The short value is: "+c);
		
		
		
		
		
	}

}
